import React from 'react';
import Swiper from 'react-id-swiper';
import 'swiper/css/swiper.css';
import './cardSwiper.scss'
// import { url } from 'inspector';

const CHAPTERS = {'UXD Track': 5, 'Design Track': 2, 'Web Design Track': 3}

class CardSwiper extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
			sorted: [],
			params : {
				direction: 'horizontal',
				effect: 'coverflow',
				grabCursor: true,
				centeredSlides: true,
				slidesPerView: 4,
				coverflowEffect: {
					rotate: -20,
					stretch: 0,
					depth: 80,
					modifier: 1,
					slideShadows: true
				},
				pagination: {
					el: '.swiper-pagination',
        	clickable: true,
        	dynamicBullets: true
				},
				// mousewheel : {
				// 	forceToAxis: true,
				// 	invert: true
				// }
			},
			params2: {
				direction: 'vertical',
				slidesPerView: 'auto',
				centeredSlides: true,
				pagination: {
					el: '.swiper-pagination',
					clickable: true,
				},
				mousewheel: {
					forceToAxis: true
				}
			},

		}
	}

	generateCards () {
		// console.log(this.state.sorted.length, 'generate sorted')
		// if (this.state.sorted) {this.setState({sorted: []})}
		let stuff = []
		for (let i = 1; i < CHAPTERS[this.props.active_topic]; i++) {
			let segments = this.props.data.filter(item => item[0] == i.toString());
			stuff.push(segments);
		}

		return (
			<div className="cardsWrapper">
				<Swiper {...this.state.params2}>
					{stuff.map((chapter, index) =>
						<div className="HELLLOOO" key={index}>
							<Swiper {...this.state.params} id={index} key={chapter.length} >
									{chapter.map((mods, index) => 
										<div className="module-container" 
											key={index} 
											onClick={() => 
												this.props.modal_data(true, {
													'title': mods[5],
													'description': mods[7],
													'requirement': mods[9] 
												})}
										>
											<img src={mods[12]}></img>
										</div>
									)}
							</Swiper>
						</div>
					)}
				</Swiper>
			</div>
		)
	}

	render () {
		let stuff = this.props.headers && this.props.data ? 
		this.generateCards()
		: ''
		return (
			stuff || ''
		)
	}
};


export default CardSwiper;
