import React from 'react';
import Swiper from 'react-id-swiper';
import 'swiper/css/swiper.css';
import './cardSwiper.scss'
import CardGenerator from '../utils/CardGenerator';
// import { url } from 'inspector';

const CHAPTERS = {'UXD Track': 5, 'Design Track': 2, 'Web Design Track': 3}

class CardSwiper extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
			sorted: [],
			params : {
				effect: 'coverflow',
				grabCursor: true,
				centeredSlides: true,
				slidesPerView: 'auto',
				coverflowEffect: {
					rotate: -20,
					stretch: 0,
					depth: 100,
					modifier: 1,
					slideShadows: false
				},
				pagination: {
					el: '.swiper-pagination'
				}
			}
		}
	}

	generateCards () {
		// console.log(this.state.sorted.length, 'generate sorted')
		// if (this.state.sorted) {this.setState({sorted: []})}
		let stuff = []
		for (let i = 1; i < CHAPTERS[this.props.active]; i++) {
			// let header = this.state.data.filter(item => item.chapter == 'C'+i.toString())
			let segments = this.props.data.filter(item => item[0] == i.toString());
			console.log('pushing, i is', i)
			// this.state.sorted.push(segments)
			stuff.push(segments);
		}
		// this.props.data.map((item, index) => 
		// 	<div key={index} style={{ backgroundImage: `url(${item[12]})`}}>
		// 		{/* <img src={item[12]} /> */}
		// 	</div>)


		// return (
		// 	<div className="swiperContainer">
		// 		<div>
		// 			<Swiper {...this.state.params}> 
		// 				{ this.state.sorted }
		// 			</Swiper>
		// 		</div>
		// 	</div>
		// )

		return (
			stuff.map((chapter, index) =>
				<div>
					{/* <div key={index} className="chapter-container" id={chapter[0].chapter}></div>
					<div className="chapter-header">{this.state.headers[index][0].title}</div> */}
					<Swiper {...this.state.params} id={index} key={index}>
							{chapter.map((mods, index) => 
								<div key={index} className="module-container">
								{/* <Link to ={`/${this.props.active_student}/${mods.code}`}> */}
									<img src={mods[12]}></img>
								{/* </Link>  */}
								{/* <Route path={`/${this.props.active_student}/${mods.code}`} component={() => this.state.clicked ? <RenderInfo info={chapter[index]} context={this} /> : '' } />            */}
							</div>
							)}
					</Swiper>
				</div>
			)
		)
	}

	render () {
		return (
			this.props.headers && this.props.data ? 
				this.generateCards()
				: ''

		)
	}
};


export default CardSwiper;
