import React from 'react';
import Swiper from 'react-id-swiper';
import 'swiper/css/swiper.css';
import './cardSwiper.scss'
import CardGenerator from '../utils/CardGenerator';
// import { url } from 'inspector';

class CardSwiper extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
			data: this.props.data,
			sorted: [],
			column_titles: [],
			headers: [],
			params : {
				effect: 'coverflow',
				grabCursor: true,
				centeredSlides: true,
				slidesPerView: 'auto',
				coverflowEffect: {
					rotate: 50,
					stretch: 0,
					depth: 100,
					modifier: 1,
					slideShadows: true
				},
				pagination: {
					el: '.swiper-pagination'
				}
			}
		}
	}

	generateCards () {
		let divs = this.props.data.map((item, index) => 
			<div key={index} style={{ backgroundImage: `url(${item[12]})`}}>
				{/* <img src={item[12]} /> */}
			</div>)

		return (
			<Swiper {...this.state.params}> 
				{ divs }
			</Swiper>
		)
	}

	render () {
		return (
			this.props.headers && this.props.data ? 
				this.generateCards()
				: ''

		)
	}
};


export default CardSwiper;
