import React from 'react';
import Swiper from 'react-id-swiper';
import 'swiper/css/swiper.css';
import './navBar.scss'


class NavBar extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			params : {
				slidesPerView: 3,
				spaceBetween: 10,
				loop: true,
				// pagination: {
				// 	el: '.swiper-pagination',
				// 	clickable: true
				// },
				// navigation: {
				// 	nextEl: '.swiper-button-next',
				// 	prevEl: '.swiper-button-prev'
				// }
			},
			swiper: '',
			active: ''
		}

		this.updateSwiper = this.updateSwiper.bind(this);
	}

	updateSwiper(s) {
		this.setState({swiper: s});
		// console.log('swiper is ', s)

		s.on('transitionEnd', () => {
			// console.log(s.slides[s.activeIndex + 1].innerHTML)
			this.setState({active: s.slides[s.activeIndex + 1].innerHTML + ' Track'})
			console.log(s.activeIndex, 'active index!!')
			this.props.changeTopic(this.state.active)

		})
	}

	updateTopic() {
		console.log('updating! active is', this.state.active)
		this.props.changeTopic(this.state.active)
	}


	render() {
		return (
			<div className="navContainer" >
				<div>
				<Swiper {...this.state.params} getSwiper={(s) => {this.updateSwiper(s)}}>
					{this.props.titles.map((item, index) => 
					<div className="navItems" key={index} id={item}>{item.replace(" Track", "")}</div>)}
				</Swiper>
				</div>
			</div>
		)
	}

}

export default NavBar
