import React from 'react';
// import '../App.css';
import CardSwiper from './cardSwiper';
import 'swiper/css/swiper.css';

import { gapi } from 'gapi-script';
import { thisExpression } from '@babel/types';
// import { BrowserRouter as Router, Route, Link } from "react-router-dom";


var CLIENT_ID = '680140181637-gdfe7aqj7r1aabj8h7n1nq76eq41onll.apps.googleusercontent.com';
var API_KEY = 'AIzaSyCheO_PyYACghxdC8qT0vzClBzEk_V-1eA';
// var CLIENT_ID = '281675621296-k6tpcg8m2h9ei0vrdoh160mvgn8tgp9v.apps.googleusercontent.com';
// var API_KEY = 'AIzaSyDS4uWW_gtdcJMQ5V7lHesMmRvElqxj4-g';
var DISCOVERY_DOCS = ["https://sheets.googleapis.com/$discovery/rest?version=v4"];
var SCOPES = "https://www.googleapis.com/auth/spreadsheets.readonly";

class CardPage extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
			data: [],
			sorted: [],
      column_titles: [],
      sheets: ['UXD Track', 'Design Track', 'Web Design Track'],
      active_range: 'UXD Track',
      show: false
    }

    this.loadCards = this.loadCards.bind(this)
    this.change = this.change.bind(this)
  }

  componentDidMount() {
    gapi.load('client', () => {
      gapi.client.init({
        apiKey: API_KEY,
        clientId: CLIENT_ID,
        discoveryDocs: DISCOVERY_DOCS,
        scope: SCOPES
      }).then(() => {
        gapi.client.sheets.spreadsheets.values.get({
          spreadsheetId: '1OUu2JiKmCg-8ArTspCNklEkw-qCv05NeWRJHC_7AR00',
          range: this.state.active_range,
        }).then((response) => {
          let blob = response.result.values
					this.setState({data: blob})
          this.setState({column_titles: blob.shift()});
        }, function(response) {
            console.log('error ', response)
        });
      }, function(error) {
          console.log('error', error)
      }); 
    });
	}

  loadCards () {
    if (this.state.data) {
      this.setState({show: true}) 
    }
  }

  change () {
    if (this.state.active_range == 'UXD Track') {
      this.setState({active_range: this.state.sheets[1]})
    }
    else if (this.state.active_range == 'Design Track') {
      this.setState({active_range: this.state.sheets[2]})
    }
    else {
      this.setState({active_range: this.state.sheets[0]})
    }
  }

  render() {
    return (	
      <div>
        hi
        <button onClick={this.loadCards}>load!</button>
        {console.log('this state show', this.state.show)}
        {this.state.show ? <CardSwiper data={this.state.data} headers={this.state.column_titles}/> : 'nope'}
        <button onClick={this.change}>{this.state.active_range}</button>
      </div>
    );
  }

}

export default CardPage;
