import React from 'react';
import Swiper from 'react-id-swiper';
import 'swiper/css/swiper.css';
import './navBar.scss'


class NavBar extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			params : {
				slidesPerView: 3,
				spaceBetween: 10,
				loop: true,
				// pagination: {
				// 	el: '.swiper-pagination',
				// 	clickable: true
				// },
				// navigation: {
				// 	nextEl: '.swiper-button-next',
				// 	prevEl: '.swiper-button-prev'
				// }
			},
			swiper: ''
		}

		this.updateSwiper = this.updateSwiper.bind(this);
	}

	updateSwiper(s) {
		this.setState({swiper: s});
		console.log('swiper is ', s)

		s.on('transitionEnd', () => {
			console.log(s.slides)
			console.log(s.realIndex, 'active index!!')
		})
	}


	render() {
		return (
			<div className="navContainer" >
				<Swiper {...this.state.params} getSwiper={(s) => {this.updateSwiper(s)}}>
					<div className="navItems">1</div>
					<div className="navItems">2</div>
					<div className="navItems">3</div>
				</Swiper>
			</div>
		)
	}

}

export default NavBar
