import React, { Component } from 'react';
// import '../App.css';
import { gapi } from 'gapi-script';
// import { BrowserRouter as Router, Route, Link } from "react-router-dom";



var CLIENT_ID = '680140181637-gdfe7aqj7r1aabj8h7n1nq76eq41onll.apps.googleusercontent.com';
var API_KEY = 'AIzaSyCheO_PyYACghxdC8qT0vzClBzEk_V-1eA';
// var CLIENT_ID = '281675621296-k6tpcg8m2h9ei0vrdoh160mvgn8tgp9v.apps.googleusercontent.com';
// var API_KEY = 'AIzaSyDS4uWW_gtdcJMQ5V7lHesMmRvElqxj4-g';
var DISCOVERY_DOCS = ["https://sheets.googleapis.com/$discovery/rest?version=v4"];
var SCOPES = "https://www.googleapis.com/auth/spreadsheets.readonly";

class CardPage extends Component {

  constructor(props) {
    super(props);

    this.state = {

    }
  }


  componentDidMount() {
    gapi.load('client', () => {
      gapi.client.init({
        apiKey: API_KEY,
        clientId: CLIENT_ID,
        discoveryDocs: DISCOVERY_DOCS,
        scope: SCOPES
      }).then(function () {
        gapi.client.sheets.spreadsheets.values.get({
          spreadsheetId: '1OUu2JiKmCg-8ArTspCNklEkw-qCv05NeWRJHC_7AR00',
          range: 'UXD Track!A2:E',
        }).then(function(response) {
          console.log(response)
        }, function(response) {
            console.log('error ', response)
        });
      }, function(error) {
          console.log('error', error)
      }); 
    });
  }



  render() {
    return (
    	<div>
        {this.state.sorted.map((chapter, index) =>
          <div key={index} className="chapter-container" id={chapter[0].chapter}>
            <div style={{display: this.state.clicked ? 'none' : 'block'}} className="chapter-header">{this.state.headers[index][0].title}</div>
            {chapter.map((mods, index) => 
              <div key={index} className="module-container">
              <Link to ={`/${this.props.active_student}/${mods.code}`}><img className={`card-img ${mods[this.props.active_student] ? '': 'grey'}`} src={mods.picture} style={{display: this.state.clicked ? 'none' : 'block'}}
  onClick={this.changeState}></img></Link> 
              {/* <Route path={`/${this.props.active_student}/${mods.code}`} component={() => this.state.clicked ? <CardPage info={chapter[index]} context={this} /> : '' } />            */}
            </div>
            )}
          </div>
        )}      
        <Link to ={`/${this.props.active_student}/${this.props.order}`}><button style={{display: this.state.clicked ? 'block' : 'none'}}  onClick={this.changeState}>go back</button></Link>
      </div>
    );
  }

}

export default CardPage;
